import { combineReducers } from 'redux';
import * as types from '../types/babies'
import * as exTypes from '../types/events'

const byId = (state={},action) => {
    switch (action.type) {
        case types.baby_added:
            return {...state,[action.payload.id]:action.payload}
        case exTypes.event_added:
            const events = [...state[action.payload.id][Object.keys(state[action.payload.id])[3]],action.payload.element.id]
            return {...state,
                [action.payload.id]: {
                  ...state[action.payload.id],
                  events
                },}
        default:
            return state
    }
}

const order = (state=[],action) => {
    switch (action.type) {
        case types.baby_added:
            return [...state,action.payload.id]
        default:
            return state
    }
}

const selected = (state=0,action) =>{
    switch (action.type) {
        case types.baby_selected:
            return action.payload
        case types.baby_added:
            return action.payload.id
        default:
            return state
    }
}

const babies = combineReducers({
    byId,
    order,
    selected
})

export default babies

export const getBaby = (state,id) => state.byId[id]
export const getAllBabies = (state) => state.order.reverse().map(
    id=>getBaby(state,id)
    ).filter(baby=>baby!=null)
export const getSelectedBaby = state => state.selected;
