import { connect } from 'react-redux';
import React, { useState } from 'react';
import * as selectors from '../../reducers'
import * as actions from '../../actions/events'
import './styles.css';

const AddEvent = ({name,onSubmit,types}) => {
    const [info,changeInfo] = useState('')
    const [type,changeType] = useState('')
    return(
        <div className="addEvent">
            <h3>ADD AN EVENT FOR:   {name}</h3>
            <div >
                <select onChange={e=>changeType(e.target.value)} value={type} className="select" id="B" name="events">
                        {types.map(type => (
                        <option key={type[0]} value={type[1]}>{type[1]}</option> 
                        ))}
                    </select>            
                <input
                    className="myInput"
                    type="text"
                    placeholder="Write a description of the event"
                    value={info}
                    onChange={e => changeInfo(e.target.value)}
                />
                <button className="button" type="submit" onClick={
                    () => onSubmit(type,info)
                }>
                    {'+'}
                </button>
            </div>   
        </div>

    )
}

export default connect(
    state=>({
        name: selectors.getBaby(state,selectors.getSelectedBaby(state))[Object.keys(selectors.getBaby(state,selectors.getSelectedBaby(state)))[1]],
        types: [[0,'poop'],[1,'pee'],[2,'food'],[3,'sleep']]
    }),
    dispatch=>({
        onSubmit(type,info){
            dispatch(actions.add_event(type,new Date(),info))
        }
    })
)(AddEvent)
