import { v4 as uuidv4 } from 'uuid';
import * as types from '../types/babies'

export const add_baby = (name,lastname,events) => ({
    type: types.baby_added,
    payload:{
        id: uuidv4(),
        name,
        lastname,
        events
    }
})

export const update_baby = (id,name,lastname,events) =>({
    type: types.baby_updated,
    payload:{
        id,
        name,
        lastname,
        events
    }
})

export const select_baby = (id) => ({
    type: types.select_baby,
    payload: id
})
